select Company.company_code,Company.founder ,count(distinct(Lead_manager.lead_manager_code)), count(distinct(Senior_manager.senior_manager_code)), count(distinct(Manager.manager_code)), count(distinct(Employee.employee_code)) from Company inner join Lead_manager on Company.company_code=Lead_manager.company_code inner join Senior_manager on Lead_manager.lead_manager_code=Senior_manager.lead_manager_code inner join Manager on Senior_Manager.senior_manager_code=Manager.senior_manager_code inner join Employee on Manager.manager_code=Employee.manager_code
group by Company.company_code,Company.founder order by Company.company_code ;
